css:
/*Tarja Informações*/
.info-bar {
    background: #ffffff;
    color: #000;
    margin-left: auto;
    margin-right: auto;
    max-width: var(--content-max-width);
    width: 100%;
}

/*Tamanho FAQ*/
.pt-1, .py-1 {
    padding-top: 0rem;
    margin-top: 0px;
}

/*remover opções de pagamento página produto*/
.product__payment.card.mb-3 {
display: none
}

/*Esconder botão de cancelamento de pedido*/
.order-info__toggle {
    display: none !important;
}

/*Botão comprar pagina inicial mobile*/
@media screen and (max-width: 480px) {
.products-carousel__item  .product-card__buy.fade {
  opacity: 1 !important;
}}

/*diminuir espaço pagina inicial*/
.mb-5, .my-5 {
    margin-bottom: 2rem;
}

/*remover imagens forma de pagamento*/
.payment__stamp {
    display: none;
}
.payment__back, .payment__intermediator {
    display: none;
}

/*cor botão comprar página produto - final*/
.product__sticky-buy .btn-primary {
background-color: #36c5c4;
border-color: #36c5c4;
}
.product__sticky-buy .btn-primary:hover{
background-color: #5ed1d0;
border-color: #5ed1d0;
}

/*cor botão comprar página produto - inicio*/
.product__buy button {
color: #ffffff;
background-color: #36c5c4;
border-color: #36c5c4;
width: 100%;
}
.product__buy button:hover {
background: #5ed1d0;
border-color: #5ed1d0;
}

/*cor botão comprar página inicial*/
.product-card__buy button {
background-color: #36c5c4;
border-color: #36c5c4;
color: #fff;
border-radius: 0.1rem;
font-size: 0.8rem;
padding: 0.36rem 0.75rem;
}
.product-card__buy button:hover {
background-color: #5ed1d0;
border-color: #5ed1d0;
}

/*cor botão finalizar comprar carrinho*/
.btn-primary {
    background-color: #36c5c4;
    border-color: #36c5c4;
}
.btn-primary.focus, .btn-primary:focus, .btn-primary:hover {
    background-color: #5ed1d0;
    border-color: #5ed1d0;
}

/*cor botão finalizar comprar dentro carrinho*/
.btn-success {
    background-color: #36c5c4;
    border-color: #36c5c4;
    color: var(--white);
}
.btn-success.focus, .btn-success:focus, .btn-success:hover {
    background-color: #5ed1d0;
    border-color: #5ed1d0;
    color: var(--white);
}

/*tag circulo desconto*/
.product-card__offer-stamp {
    border-radius: 1rem!important;
    color: #000000!important;
    background-color: #c6eaf0;
    line-height: 1.8;
    font-weight: 100;
}

/*Cor descrição página produto*/
@media (max-width: 990px)
#product-description .lead, h1 {
    font-size: calc(var(--h1)*.6)!important;
}
#product-description .lead {
    color: #f2a1b2;
    margin-bottom: 0;
    font-weight: 700;
}

/*Cor especificação página produto*/
@media (max-width: 990px)
#product-specs .lead, h1 {
    font-size: calc(var(--h1)*.6)!important;
}
#product-specs .lead {
    color: #f2a1b2;
    margin-bottom: 0;
    font-weight: 700;
    margin: 0px 0px 35px 0px;
}

/*Tamanha do preço na página produto*/
.prices--big .prices__value {
    font-size: calc(var(--h1)*.8);
}

/*Tamanha do descrição na página produto*/
.h1, h1 {
    font-size: 2rem;
}
@media (max-width: 990px) {
.h1, h1 {
    font-size: calc(var(--h1)*.6)!important;
 }
}

/*Negrito titulo informações pagina inicial*/
.info-bar__title {
    font-weight: 600;
}

/*Cor search*/
.header__search {
    border-bottom: 1px solid #000000;
}
/*Cor cabeçalho desktop*/
.header {
    background: #ffffff;
    border-bottom: 0px solid #ffffff;
}

/*Cor menu desktop*/
.header__category:hover {
    background: #ffffff;
    text-decoration: none;
    color: #f2a1c3;
}

/*espaço menu desktop*/
.header__nav--full a {
    padding: 10px 30px 0px 30px;
}

/*fonte menu desktop*/
.header__nav a {
    font-weight: normal;
}

/*tamanho topbar cabeçalho*/
.top-bar__countdown {
    padding-bottom: 4px;
    padding-top: 4px;
    font-size: 14px;
}
@media only screen and (max-width: 900px){
.top-bar__countdown {
    padding-bottom: var(--spacer-2)!important;
    padding-top: var(--spacer-2)!important;
    font-size: 12px!important;
  }
}



.joiasmilan-loja-joiasmilan-3-x-specLine:nth-child(odd) {
    background: rgba(222,163,156,.3);
}

/*especificação página produto*/
.joiasmilan-loja-joiasmilan-3-x-specLine {
    display: flex;
    align-items: flex-start;
    justify-content: space-between;
    padding: 1% 5%;
    border-radius: 99px;
    margin: 0px 500px 0px 0px;
}

@media screen and (max-width: 900px) {
.joiasmilan-loja-joiasmilan-3-x-specLine {
    padding: 3% 5%!important;
    margin: 0px 0px 0px 0px!important;
  }
}

/*cor disponibilidade estoque*/
.product__short-stock {
    background: none;
    font-weight: normal;
}

/*selos rodapé*/
@media (max-width: 990px) {
.app__stamps>.stamps {
    justify-content: center;
 }
}
.stamps {
    display: flex;
}
.stamps {
    flex-wrap: wrap;
}
@media (max-width: 990px) {
.stamps>li {
    flex: 0 0 calc(50% - 10px);
    min-width: auto;
    width: auto;
 }
}  
.stamps>li {
    align-items: center;
    background: #fff0;
    border-radius: 3px;
    justify-content: space-between;
    margin: 0 10px 10px 0!important;
    padding: 10px;
    text-align: center;
}
@media (min-width: 990px) {
.app__stamps>.stamps {
    justify-content: center;
 }
}
@media (min-width: 990px) {
.app__footer .stamps>li {
    flex: 0 0 auto;
 }
}

.stamps>li #seloEconfy>img {
    max-width: unset!important;
    width: unset!important;
}

/*cor icones informações*/
.rounded-icon {
    border: 1px solid #f8f9f900;
    color: #f2a1b2;
}

/*preço página inicial*/
.prices__value {
    line-height: 1.5;
}
.prices, .prices__installments {
    font-weight: 500;
}
.prices__discount {
    display: none;
}

/*cor sub topbar*/
.top-bar__nav {
    color: #373435;
}
.top-bar {
    background: #ffffff;
}

/*tirar botão finalizar compra minicart*/
.minicart__btn-checkout {
    display: none;
}
/*cor e frase botão carrinho minicart*/
.minicart__btn-cart {
    color: #ffffff;
    background-color: #36c5c4;
    border-color: #36c5c4;
    padding: 0.5rem 1rem;
    font-size: 0;
}
.minicart__btn-cart:hover {
    color: #ffffff;
    background-color: #5ed1d0;
    border-color: #5ed1d0;
}  
.minicart__btn-cart:after {
    content: 'Finalizar compra';
    font-size: 16px !important;
    font-weight: 500;
}

/*retirada da borda rodapé*/
.footer {
    border-top: none;
}
/* FOOTERBAR */
.footerbar a > *{
    line-height:18px;
    color: #FFF;
}
.footerbar img{
    width: 50px;
    min-width: auto;
    display: block;
    margin: 0 auto;
}
.footerbar a{
    display:flex;
    gap:5px;
    text-align:center;
    flex-direction:column
}
.footerbar a  b{font-size:16px;}
.footerbar a  small{font-size:14px;display:block;}
.footerbar{
    padding: 10px 0;
    border-bottom: 1px solid #f1f1f1;
    background-color: #373435;
}
@media(min-width:990px){
    .footerbar{padding: 10px 0 20px 0;}
    .footerbar .row-flex{
        display:flex;
        justify-content:space-between;
        align-items:center;
        gap:60px;
    }
    .footerbar a{max-width:200px;}
    .footerbar a  b{font-size:14px;}
    .footerbar a  small{font-size:12px;}
}
@media(max-width:990px){
    #apx_economia span{margin-left:auto!important;margin-right:auto!important;}
    .footerbar{padding:0 10px;}
    .footerbar a img{margin:0;}
    .footerbar .row-flex > div:not(:last-child){border-bottom: 1px solid #5a5a5a;}
    .footerbar a{
        width: 100%;
        justify-content: left;
        min-width: 100%;
        flex-direction: row;
        text-align: left;
        gap: 30px;
        padding: 17px;
        align-items: center;
    }
    .footerbar small{display:block;}
}

/*Ambiente seguro carrinho*/
.app__safe {
    display: inline;
}
.app__contact {
    display: none;
}
/*cabeçalho branco carrinho*/
.app__header {
    background: #ffffff;
}

/*Ver mais página inicial*/
.products-carousel__title a:after {
    color: #212529!important;
    content: " + Ver mais";
    font-size: .8rem;
    font-weight: 700;
    padding: 0.5rem;
}



.prices:not(.prices--big) .prices__installments {
    font-weight: 500;
}

.prices .prices__installments span {
    color: #212529;
    font-size: var(--font-size-lg);
    font-weight: 500;
}


.prices .prices__value {
    font-size: 12px;
    /* order: 3; */
    font-weight: 500;
}

.prices .prices__compare {
    font-size: 12px;
}
