css:
/*Tarja Informações*/
.info-bar {
    background: #ffffff;
    color: #000;
    margin-left: auto;
    margin-right: auto;
    max-width: var(--content-max-width);
    width: 100%;
}

/*Tamanho FAQ*/
.pt-1, .py-1 {
    padding-top: 0rem;
}

/*remover opções de pagamento página produto*/
.product__payment.card.mb-3 {
display: none
}
/*remover compartilhamento página produto*/
#product-actions .btn.share {
display: none
}

/*Esconder botão de cancelamento de pedido*/
.order-info__toggle {
    display: none !important;
}

/*Botão comprar pagina inicial mobile*/
@media screen and (max-width: 480px) {
.product-card__buy.fade {
  opacity: 1 !important;
}}

/*diminuir espaço pagina inicial*/
.mb-5, .my-5 {
    margin-bottom: 1rem;
}


@media (max-width: 990px)
.stamps>li {
    flex: 0 0 calc(50% - 10px);
    min-width: auto;
    width: auto;
}

.stamps>li {
    align-items: center;
    background: #fff;
    border-radius: 3px;
    flex: 0 0 31%;
    justify-content: space-between;
    margin: 0 10px 10px 0!important;
    padding: 10px;
    text-align: center;
}
.stamps>li {
    max-width: 180px;
    min-width: 120px;
    padding: var(--spacer-3) 0;
}

.stamps {
    display: flex;
    flex-wrap: wrap;
    list-style: none;
    margin: 0;
    max-width: 100%;
    overflow-x: auto;
    padding: 0;
    flex-direction: row;
    justify-content: center;
}
