css:
/*cor icone sacola de compras*/
main .header__buttons .badge-primary {
    background-color: #373435;
}

/*Tamanho FAQ*/
.pt-1, .py-1 {
    padding-top: 0rem;
    margin-top: 0px;
}

/*remover opções de pagamento página produto*/
.product__payment.card.mb-3 {
display: none
}

/*remover opções de compartilhamento página produto*/
#product-actions .btn.share {
display: none
}

/*Esconder botão de cancelamento de pedido*/
.order-info__toggle {
    display: none !important;
}

/*Botão comprar pagina inicial mobile*/
@media screen and (max-width: 480px) {
.products-carousel__item  .product-card__buy.fade {
  opacity: 1 !important;
}}

/*diminuir espaço pagina inicial*/
.mb-5, .my-5 {
    margin-bottom: 2rem;
}

/*remover imagens forma de pagamento*/
.payment__stamp {
    display: none;
}
.payment__back, .payment__intermediator {
    display: none;
}

/*cor botão comprar página produto - final*/
.product__sticky-buy .btn-primary {
background-color: #36c5c4;
border-color: #36c5c4;
}
.product__sticky-buy .btn-primary:hover{
background-color: #5ed1d0;
border-color: #5ed1d0;
}

/*cor botão comprar página produto - inicio*/
.product__buy button {
color: #ffffff;
background-color: #36c5c4;
border-color: #36c5c4;
width: 100%;
}
.product__buy button:hover {
background: #5ed1d0;
border-color: #5ed1d0;
}

/*cor botão comprar página inicial*/
.product-card__buy button {
background-color: #36c5c4;
border-color: #36c5c4;
color: #fff;
border-radius: 0.1rem;
font-size: 0.8rem;
padding: 0.36rem 0.75rem;
}
.product-card__buy button:hover {
background-color: #5ed1d0;
border-color: #5ed1d0;
}
.product-card__buy {
    margin-top: 10px;
}

/*cor botão finalizar comprar carrinho*/
.btn-primary {
    background-color: #36c5c4;
    border-color: #36c5c4;
}
.btn-primary.focus, .btn-primary:focus, .btn-primary:hover {
    background-color: #5ed1d0;
    border-color: #5ed1d0;
}

/*cor botão finalizar comprar dentro carrinho*/
.btn-success {
    background-color: #36c5c4;
    border-color: #36c5c4;
    color: var(--white);
}
.btn-success.focus, .btn-success:focus, .btn-success:hover {
    background-color: #5ed1d0;
    border-color: #5ed1d0;
    color: var(--white);
}

/*tag circulo desconto*/
.product-card__offer-stamp {
    border-radius: 1rem!important;
    color: #ffffff!important;
    min-width: 55px!important;
    font-size: 11px;
    font-weight: 100;
    line-height: 1.7;
}

main .product-card__offer-stamp .i-arrow-down:before {
    font-size: 12px;
    content: ""!important;
}

/*cor tarja frete*/
.list-group-item.active {
    border: 1px solid #e8e8e8;
    color: #000000;
    z-index: 2;
    align-items: center;
    background: var(--light);
    border-left: 3px solid var(--success);
    border-radius: 0.25rem;
    display: flex;
    justify-content: space-between;
}

/*personalização oferta relampago mobile*/
.timer__notes {
    color: #725027;
    margin-top: 0px;
    font-size: 20px;
    font-weight: 700;
}
.offers-timer {
    border-bottom: 0px;
    padding-bottom: var(--spacer-2);
    background: linear-gradient(0deg,#fff 0,#dfc277);
    border-radius: 5px;
}
/*Botão comprar oferta relampago mobile*/
@media screen and (max-width: 480px) {
.product-card__buy {
    opacity: 1 !important;
}}
.product-card__name {
    --max-lines: 2;
}

.product-card {
    BACKGROUND-COLOR: white;
}

/*formatação tempo outlet*/
.html-clearfix {
    margin-top: 0px;
    margin-bottom: 0px;
}

/*pontos cashback*/
.product__prices .prices .prices__points {
    color: #28a745;
    display: none;
}

/*pontos cashback carrinho*/
.prices__points {
    color: #28a745;
}
.cart .prices__points {
    display: none;
}
.checkout .prices__points {
    font-weight: 400;
}

/*cabeçalho branco carrinho*/
.app__header {
    background: #ffffff;
}

/*Ambiente seguro carrinho*/
.app__safe {
    display: inline;
}
.app__contact {
    display: none;
}

/* FOOTERBAR */
.footerbar a > *{
    line-height:18px;
    color: #fff;
}
.footerbar img{
    width: 50px;
    min-width: auto;
    display: block;
    margin: 0 auto;
}
.footerbar a{
    display:flex;
    gap:5px;
    text-align:center;
    flex-direction:column
}
.footerbar a  b{font-size:16px;}
.footerbar a  small{font-size:14px;display:block;}
.footerbar{
    padding: 10px 0;
    border-bottom: 1px solid #373435;
    background-color: #373435;
}
@media(min-width:990px){
    .footerbar{padding: 10px 0 20px 0;}
    .footerbar .row-flex{
        display:flex;
        justify-content:space-between;
        align-items:center;
        gap:60px;
    }
    .footerbar a{max-width:200px;}
    .footerbar a  b{font-size:14px;}
    .footerbar a  small{font-size:12px;}
}
@media(max-width:990px){
    #apx_economia span{margin-left:auto!important;margin-right:auto!important;}
    .footerbar{padding:0 0px;}
    .footerbar a img{margin:0;}
    .footerbar .row-flex > div:not(:last-child){border-bottom: 1px solid #5a5a5a;}
    .footerbar a{
        width: 100%;
        justify-content: left;
        min-width: 100%;
        flex-direction: row;
        text-align: left;
        gap: 30px;
        padding: 17px;
        align-items: center;
    }
    .footerbar small{display:block;}
}

/*tirar botão finalizar compra minicart*/
.minicart__btn-checkout {
    display: none;
}

/*cor e frase botão carrinho minicart*/
.minicart__btn-cart {
    color: #ffffff;
    background-color: #36c5c4;
    border-color: #36c5c4;
    padding: 0.5rem 1rem;
    font-size: 0;
}
.minicart__btn-cart:hover {
    color: #ffffff;
    background-color: #5ed1d0;
    border-color: #5ed1d0;
}  
.minicart__btn-cart:after {
    content: 'Finalizar compra';
    font-size: 16px !important;
    font-weight: 500;
}

/*fonte do topbar*/
main .top-bar__countdown {
    font-weight: 500;
    text-decoration: none;
}

/*selos rodapé*/
@media (max-width: 990px) {
.app__stamps>.stamps {
    justify-content: center;
 }
}
.stamps {
    display: flex;
}
.stamps {
    flex-wrap: wrap;
}
@media (max-width: 990px) {
.stamps>li {
    flex: 0 0 calc(50% - 10px);
    min-width: auto;
    width: auto;
 }
}  
.stamps>li {
    align-items: center;
    background: #fff0;
    border-radius: 3px;
    justify-content: space-between;
    margin: 0 10px 10px 0!important;
    padding: 10px;
    text-align: center;
}
@media (min-width: 990px) {
.app__stamps>.stamps {
    justify-content: center;
 }
}
@media (min-width: 990px) {
.app__footer .stamps>li {
    flex: 0 0 auto;
 }
}
.stamps>li #seloEconfy>img {
    max-width: unset!important;
    width: unset!important;
}

/*cor icone menu celular*/
.header__toggler-icon {
    color: #373435;
}

/*tamanho imagem página produto*/
.html-clearfix .lozad.show {
    width: 25%;
}
@media screen and (max-width: 900px) {
.html-clearfix .lozad.show {
    width: 60%;
  }
}

/*especificação página produto*/
.joiasmilan-loja-joiasmilan-3-x-specLine:nth-child(odd) {
    background: #f2a1b24d;
}

.joiasmilan-loja-joiasmilan-3-x-specLine {
    display: flex;
    align-items: flex-start;
    justify-content: space-between;
    padding: 1% 5%;
    border-radius: 99px;
    margin: 0px 500px 0px 0px;
    font-size: 14px;
}

@media screen and (max-width: 900px) {
.joiasmilan-loja-joiasmilan-3-x-specLine {
    padding: 3% 5%!important;
    margin: 0px 0px 0px 0px!important;
  }
}

/*Cor especificação página produto*/
@media (max-width: 990px)
#product-specs .lead, h1 {
    font-size: calc(var(--h1)*.6)!important;
}
#product-specs .lead {
    color: #f2a1b2;
    margin-bottom: 0;
    font-weight: 700;
    margin: 0px 0px 35px 0px;
}

/*Cor descrição página produto*/
#product-description .lead {
    color: #000000;
    text-transform: initial;
    font-weight: bold;
    font-size: 18px;
    letter-spacing: 0.5px;
    font-family: Montserrat,-apple-system,system-ui,BlinkMacSystemFont,Segoe UI,Roboto,Helvetica Neue,Arial,sans-serif;
}

/*Cor texto página produto*/
.product-description .html-clearfix {
    color: #333333;
}

/*tamanho produtos recomendados página produto*/
main .page--products .recommended-products .products-carousel__title {
    font-size: 22px;
    font-weight: 600;
}

/*Cor preço comparação página inicial*/
main .product-card .prices__compare s {
    color: #b7b7b7;
}

/*fonte título produto página inicial mobile*/
@media (max-width: 767px){
main .product-card__name {
    font-size: 14px!important;
  }}

/*retira linha divisória em cima calcular frete página produto*/
main .product #product-actions>div .mb-3:not(.product__payment), main .product #product-actions>div article {
    display: none;
}

/*Cor submenu*/
.header__nav .header__submenu a {
    color: #373435;
    font-size: 13px;
    padding: 5px 0px;
}
.header__nav .header__submenu a:hover {
    color: #f2a1b2;
}
.header__nav .header__category:hover {
    color: #f2a1b2;
}
div>.header__nav .header__category:hover {
    color: #f2a1b2;
}

/*Tarja Informações*/
main .info-bar {
    background-color: #fff;
    box-shadow: none;
    color: #000;
    padding-top: 10px;
}
@media(max-width:990px){
main .info-bar__text {
    font-size: 11px!important;
  }
}
@media(max-width:990px){
main .info-bar__item div .info-bar__title {
    font-size: 13px!important;
  }
}
main .info-bar__item div {
    font-size: 12px;
    font-weight: 500;
    margin-left: 0;
    text-transform: none;
}
main .info-bar__item div .info-bar__title {
    font-family: inherit;
    font-size: 14px;
    font-weight: 600;
    font-style: normal;
    color: #000000;
}

/*Tarja informações alinhamento*/
@media (max-width: 575.98px){
main .info-bar__nav .col-auto {
    padding-left: 17px;
    padding-right: 17px;
  }}

/*cor icones informações*/
.rounded-icon {
    color: #f2a1b2;
    border: 2px solid #f2a1b2;
    margin-right: 6px;
}

/*posição botão comprar mobile*/
.product-card--small .product-card__buy {
    position: initial;
    right: 0;
}

@media (max-width: 575.98px){
.product-card__prices {
    display: flex!important;
    flex: 1!important;
    flex-direction: column;
    justify-content: flex-end;
}}

/*ajustes menu*/
.menu__link {
    background: #f2a1b2;
    border-radius: 10px;
    font-weight: 500;
    color: #ffffff;
    border-top: 0;
}
.menu__item:hover {
    background: #fbece8;
}
.menu__item {
    border-bottom: var(--border-width) solid #fbece8;
}

/*barra searche desktop*/
main .header__search {
    border-bottom: 0;
    background: 0;
}
.header__search-input {
    border: 1px solid #d0d1d2;
}
main form .form-control {
    font-size: 14px;
}

/*menu desktop*/
div>.header__nav--full {
    padding-top: 20px;
}
div>.header__nav .header__category {
    font-size: 13px;
}
div>.header__nav--full a {
    padding: 0 30px;
    margin-top: 0px;
}
div>.header__nav--full:before {
    margin-top: -8px;
}
.header__container {
    margin-bottom: 0;
}

/*oferta relâmpago*/
.timer__count {
    background: #00000000;
    border-radius: 15px;
    color: #ffffff;
    display: inline-block;
    font-size: var(--h3);
    padding: var(--spacer-1) var(--spacer-2);
    font-weight: 600;
}
.timer__icon {
    display: none;
}
.timer__count {
    margin-top: 10px;
}
.timer__count:before {
    content: "ACABAM EM: ";
    font-size: 15px;
    color: #725027;
}
.timer__notes {
    margin-top: 15px;
    order: 1;
}
.timer__count {
    margin-top: 0px;
    order: 2;
}
.timer {
    display: grid;
}
@media (max-width: 575.98px){
.timer {
    margin-bottom: var(--spacer-3);
    text-align: center;
    margin-right: 0px;
}}

/*principais categorias página principal*/
@media (min-width: 992px){
.col-12.col-lg-2.mb-3.mb-lg-0 {
    max-width: 12%!important;
 }
}
.col-12.col-lg-2.mb-3.mb-lg-0 {
    max-width: 25%;
    margin: auto;
    padding-bottom: 20px;
}

.col-12.col-lg-2.mb-3.mb-lg-0:before {
    position: absolute;
    content: "";
    top: 0px;
    right: calc(100% + -10px);
    transform: skewX(-25deg);
    background: linear-gradient(90deg, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 0.4) 50%, rgba(255, 255, 255, 0) 100%);
    height: 70%;
    width: 30px;
    cursor: pointer;
}
.col-12.col-lg-2.mb-3.mb-lg-0:hover:before {
    right: -100px;
    transition: all 500ms linear 0s;
}

@media (min-width: 992px)
.mb-lg-0, .my-lg-0 {
    margin: auto;
    padding-bottom: 20px;
}

@media (min-width: 768px){
.page-title.container.mt-4.mb-4.mb-lg-5 {
    padding-top: 50px;
  }}

.page-title.container.mt-4.mb-4.mb-lg-5 {
    text-align: center;
    font-family: sans-serif;
    padding-top: 20px;
}

@media (min-width: 768px){
.page-title__head {
    display: block;
  }}

.page-title.container.mt-4.mb-4.mb-lg-5 {
    text-align: center;
    font-family: sans-serif;
    padding-top: 20px;
    margin-bottom: auto;
    margin-top: 0px;
}

/*fonte preço produto página inicial*/
main .product-card .prices__value {
    font-size: 18px;
    color: #373435;
    font-weight: 700;
    margin-top: 3px;
    margin-bottom: 3px;
}
/*fonte preço comparação página inicial*/
main .product-card .prices__compare s {
    font-size: 14px;
}
/*espaço preço comparação página inicial*/
main .product-card .prices__compare {
    margin-right: 20px;
}

/*preço parcela página inicial*/
.product-card .prices:not(.prices--big) .prices__installments {
    font-weight: 600;
    color: #373435;
}

.product-card .prices__installments:before {
    content: "Em até";
    font-weight: 300;
    font-size: 13px;
    color: #373435;
}

/*cor preço parcela e pix página produto*/
.product__prices .prices__discount span, .product__prices .prices__installments span {
    color: #373435;
}
.product-card .prices__discount {
    color: #373435;
    font-weight: 600;
    font-size: 14px;
    margin-top: 3px    
}

.product-card .prices__discount-label {
    font-size: 13px;
    font-weight: 300;
    color: #373435;
}

/*fonte título página inicial*/
main .products-carousel__title {
    font-family: sans-serif;
    font-weight: 700;
    font-size: 22px;
}

/*fonte categorias página inicial*/
main .page-title__head h1 {
    font-family: sans-serif;
    line-height: 20px;
    font-size: 22px;
    font-weight: 600;
    color: #373435;
}
@media (max-width: 575.98px){
main .page-title__head h1 {
    padding-bottom: 10px;
  }}
/*página extra cashback parte 1*/
@media (max-width: 990px){
#ppts .ppts-divider h1.ppts-title {
    font-size: 45px!important;
 }
}
@media (min-width: 992px){
.pt-lg-3, .py-lg-3 {
    padding-top: 1rem!important;
 }
}

