    height: 635px;
    margin-bottom: -6rem;
    margin-left: -10rem;
    margin-top: -7rem;
    overflow: hidden;
    position: relative;
    width: -moz-fit-content;
    width: fit-content;
}

#ppts .ppts-imageCrop img {
    margin: -82px 0 0;
    position: relative;
