#ppts .ppts-divider h1.ppts-title {
    color: #000000!important;
    font-size: 60px;
    font-weight: 900;
    line-height: normal;
    margin: 0;
    text-align: center;
}

@media (max-width: 990px){
#ppts .ppts-divider .ppts-title+p {
    font-size: 24px!important;
 }
}
#ppts .ppts-divider .ppts-title+p {
    color: #646457;
    font-size: 39.06px;
    font-weight: 400;
    text-align: center;
}

.align-items-center {
    align-items: center;
}

.justify-content-center {
    justify-content: center;
}

#ppts .ppts-compare small {
    color: #000000;
    display: block;
    font-size: 34px;
    font-weight: 600;
    line-height: 42px;
