    font-weight: 900;
    line-height: normal;
    margin: 1rem auto;
    padding: 0.5rem 1.5rem;
    position: relative;
    width: -moz-fit-content;
    width: fit-content;
}
@media (max-width: 990px){
#ppts .ppts-compare span {
    font-size: 25px!important;
    line-height: normal!important;
 }
}
#ppts .ppts-compare span {
    color: #646457;
    display: block;
    font-size: 36px;
    font-weight: 400;
    line-height: 47px;
    text-align: center;
}

#ppts .ppts-compare .detail img {
    position: absolute;
}

#ppts .ppts-como_participar-bg {
    background: #f2a1b2;
}

@media (max-width: 990px){
#ppts .ppts-como_participar .ppts-title {
    font-size: 42px!important;
    padding: 3rem 0 5rem!important;
    text-align: center!important;
 }
}
#ppts .ppts-como_participar .ppts-title {
    color: #000000;
    font-size: 61.04px;
    font-weight: 900;
    line-height: 73px;
    margin: 3rem 0;
}
@media (max-width: 990px){
#ppts .ppts-como_participar ul {
    flex-direction: column!important;
    margin: -6rem 0 0!important;
    padding: 0 2rem!important;
    width: 100%!important;
 }
