}
#ppts .ppts-como_participar ul li b {
    color: #000000;
    font-size: 18px;
    line-height: 23px;
}
#ppts .ppts-como_participar ul li p {
    color: #6a6a57;
    font-size: 14px;
    line-height: 22px;
}
@media (max-width: 990px){
#ppts .ppts-info {
    margin: 2rem auto 0 0!important;
    text-align: center!important;
 }
}
#ppts .ppts-info {
    color: #000000;
    font-size: 18px;
    font-weight: 900;
    line-height: 23px;
}
#ppts .ppts-info small {
    color: #646457;
    font-size: 15.36px;
    font-weight: 400;
    line-height: 20px;
}
#ppts .ppts-benefits img {
    display: block;
    margin: 0 auto 1rem;
}
#ppts .ppts-benefits strong {
    color: #000000;
    display: block;
    font-size: 16px;
    font-weight: 600; 
    line-height: 24px;
    margin: 0 auto;
    max-width: 100%;
    text-align: center;
    width: 220px;
}

/*página extra cashback parte 2*/
#ppts .ppts-title {
    color: #000000;
    font-size: 40px;
    font-weight: 900;
}
.steps h3 {
    margin: 0 0 2rem;
}
.text-center {
    text-align: center;
}
.steps p {
    color: #646457;
    font-size: 25px;
    font-weight: 400;
    line-height: 40px;
    margin-bottom: 0;
    margin-left: auto;
    margin-right: auto;
    max-width: 100%;
    text-align: center;
    width: 1000px;
}

