css:
/*cor icone sacola de compras*/
main .header__buttons .badge-primary {
    background-color: #f2a1b2;
}

/*Tamanho FAQ*/
.pt-1, .py-1 {
    padding-top: 0rem;
    margin-top: 0px;
}

/*remover opções de pagamento página produto*/
.product__payment.card.mb-3 {
display: none
}

/*remover opções de compartilhamento página produto*/
#product-actions .btn.share {
display: none
}

/*Esconder botão de cancelamento de pedido*/
.order-info__toggle {
    display: none !important;
}

/*Botão comprar pagina inicial mobile*/
@media screen and (max-width: 480px) {
.products-carousel__item  .product-card__buy.fade {
  opacity: 1 !important;
}}

/*diminuir espaço pagina inicial*/
.mb-5, .my-5 {
    margin-bottom: 2rem;
}

/*remover imagens forma de pagamento*/
.payment__stamp {
    display: none;
}
.payment__back, .payment__intermediator {
    display: none;
}

/*cor botão comprar página produto - final*/
.product__sticky-buy .btn-primary {
background-color: #36c5c4;
border-color: #36c5c4;
}
.product__sticky-buy .btn-primary:hover{
background-color: #5ed1d0;
border-color: #5ed1d0;
}

/*cor botão comprar página produto - inicio*/
.product__buy button {
color: #ffffff;
background-color: #36c5c4;
border-color: #36c5c4;
width: 100%;
}
.product__buy button:hover {
background: #5ed1d0;
border-color: #5ed1d0;
}

/*cor botão comprar página inicial*/
.product-card__buy button {
background-color: #36c5c4;
border-color: #36c5c4;
color: #fff;
border-radius: 0.1rem;
font-size: 0.8rem;
padding: 0.36rem 0.75rem;
}
.product-card__buy button:hover {
background-color: #5ed1d0;
border-color: #5ed1d0;
}

/*cor botão finalizar comprar carrinho*/
.btn-primary {
    background-color: #36c5c4;
    border-color: #36c5c4;
}
.btn-primary.focus, .btn-primary:focus, .btn-primary:hover {
    background-color: #5ed1d0;
    border-color: #5ed1d0;
}

/*cor botão finalizar comprar dentro carrinho*/
.btn-success {
    background-color: #36c5c4;
    border-color: #36c5c4;
    color: var(--white);
}
.btn-success.focus, .btn-success:focus, .btn-success:hover {
    background-color: #5ed1d0;
    border-color: #5ed1d0;
    color: var(--white);
}

/*tag circulo desconto*/
.product-card__offer-stamp {
    border-radius: 1rem!important;
    color: #ffffff!important;
    background-color: #000000;
    line-height: 1.5;
    font-weight: 100;
}

/*cor tarja frete*/
.list-group-item.active {
    background-color: #f2a1b2;
    border-color: #f2a1b2;
    color: var(--primary-lightest-yiq);
    z-index: 2;
}

/*personalização oferta relampago mobile*/
.timer__notes {
    color: #725027;
    margin-top: var(--spacer-2);
    font-size: 20px;
    font-weight: 700;
}
.offers-timer {
    border-bottom: 0px;
    padding-bottom: var(--spacer-2);
    background: linear-gradient(0deg,#fff 0,#dfc277);
    border-radius: 5px;
}
/*Botão comprar oferta relampago mobile*/
@media screen and (max-width: 480px) {
.product-card__buy {
    opacity: 1 !important;
}}
.product-card__name {
    --max-lines: 2;
}

.product-card {
    BACKGROUND-COLOR: white;
}

/*formatação tempo outlet*/
.html-clearfix {
    margin-top: 0px;
    margin-bottom: 0px;
}

/*pontos cashback*/
.product__prices .prices .prices__points {
    color: #28a745;
    display: none;
}

/*cabeçalho branco carrinho*/
.app__header {
    background: #ffffff;
}

/*Ambiente seguro carrinho*/
.app__safe {
    display: inline;
}
.app__contact {
    display: none;
}

/* FOOTERBAR */
.footerbar a > *{
    line-height:18px;
    color: #fff;
}
.footerbar img{
    width: 50px;
    min-width: auto;
    display: block;
    margin: 0 auto;
}
.footerbar a{
    display:flex;
    gap:5px;
    text-align:center;
    flex-direction:column
}
.footerbar a  b{font-size:16px;}
.footerbar a  small{font-size:14px;display:block;}
.footerbar{
    padding: 10px 0;
    border-bottom: 4px solid #f2a1b2;
    background-color: #373435;
}
@media(min-width:990px){
    .footerbar{padding: 10px 0 20px 0;}
    .footerbar .row-flex{
        display:flex;
        justify-content:space-between;
        align-items:center;
        gap:60px;
    }
    .footerbar a{max-width:200px;}
    .footerbar a  b{font-size:14px;}
    .footerbar a  small{font-size:12px;}
}
@media(max-width:990px){
    #apx_economia span{margin-left:auto!important;margin-right:auto!important;}
    .footerbar{padding:0 10px;}
    .footerbar a img{margin:0;}
    .footerbar .row-flex > div:not(:last-child){border-bottom: 0px solid #5a5a5a;}
    .footerbar a{
        width: 100%;
        justify-content: left;
        min-width: 100%;
        flex-direction: row;
        text-align: left;
        gap: 30px;
        padding: 17px;
        align-items: center;
    }
    .footerbar small{display:block;}
}

/*tirar botão finalizar compra minicart*/
.minicart__btn-checkout {
    display: none;
}

/*cor e frase botão carrinho minicart*/
.minicart__btn-cart {
    color: #ffffff;
    background-color: #36c5c4;
    border-color: #36c5c4;
    padding: 0.5rem 1rem;
    font-size: 0;
}
.minicart__btn-cart:hover {
    color: #ffffff;
    background-color: #5ed1d0;
    border-color: #5ed1d0;
}  
.minicart__btn-cart:after {
    content: 'Finalizar compra';
    font-size: 16px !important;
    font-weight: 500;
}

/*cor sub topbar*/
.top-bar__nav {
    color: #373435;
}
.top-bar {
    background: #ffffff;
}

/*selos rodapé*/
@media (max-width: 990px) {
.app__stamps>.stamps {
    justify-content: center;
 }
}
.stamps {
    display: flex;
}
.stamps {
    flex-wrap: wrap;
}
@media (max-width: 990px) {
.stamps>li {
    flex: 0 0 calc(50% - 10px);
    min-width: auto;
    width: auto;
 }
}  
.stamps>li {
    align-items: center;
    background: #fff0;
    border-radius: 3px;
    justify-content: space-between;
    margin: 0 10px 10px 0!important;
    padding: 10px;
    text-align: center;
}
@media (min-width: 990px) {
.app__stamps>.stamps {
    justify-content: center;
 }
}
@media (min-width: 990px) {
.app__footer .stamps>li {
    flex: 0 0 auto;
 }
}
.stamps>li #seloEconfy>img {
    max-width: unset!important;
    width: unset!important;
}

/*cor icone menu celular*/
.header__toggler-icon {
    color: #373435;
}

/*cor disponibilidade estoque*/
.product__short-stock {
    background: none;
    font-weight: normal;
}

/*tamanho imagem página produto*/
.html-clearfix .lozad.show {
    width: 25%;
}
@media screen and (max-width: 900px) {
.html-clearfix .lozad.show {
    width: 60%;
  }
}

/*especificação página produto*/
.joiasmilan-loja-joiasmilan-3-x-specLine:nth-child(odd) {
    background: rgba(222,163,156,.3);
}

.joiasmilan-loja-joiasmilan-3-x-specLine {
    display: flex;
    align-items: flex-start;
    justify-content: space-between;
    padding: 1% 5%;
    border-radius: 99px;
    margin: 0px 500px 0px 0px;
    font-size: 14px;
}

@media screen and (max-width: 900px) {
.joiasmilan-loja-joiasmilan-3-x-specLine {
    padding: 3% 5%!important;
    margin: 0px 0px 0px 0px!important;
  }
}

/*Cor especificação página produto*/
@media (max-width: 990px)
#product-specs .lead, h1 {
    font-size: calc(var(--h1)*.6)!important;
}
#product-specs .lead {
    color: #f2a1b2;
    margin-bottom: 0;
    font-weight: 700;
    margin: 0px 0px 35px 0px;
}

/*Cor descrição página produto*/
@media (max-width: 990px)
#product-description .lead, h1 {
    font-size: calc(var(--h1)*.6)!important;
}
#product-description .lead {
    color: #f2a1b2;
    margin-bottom: 0;
    font-weight: 700;
}

/*Cor preço comparação página inicial*/
main .product-card .prices__compare s {
    color: #969696;
}

/*retira linha divisória em cima calcular frete página produto*/
main .product #product-actions>div .mb-3:not(.product__payment), main .product #product-actions>div article {
    display: none;
}

/*Cor submenu*/
.header__nav .header__submenu a {
    color: #373435;
    font-size: 13px;
    padding: 5px 0px;
}
.header__nav .header__submenu a:hover {
    color: #f2a1b2;
}

/*Tarja Informações*/
main .info-bar {
    background-color: #fff;
    box-shadow: none;
    color: #000;
}
@media(max-width:990px){
main .info-bar__text {
    font-size: 11px!important;
  }
}
@media(max-width:990px){
main .info-bar__item div .info-bar__title {
    font-size: 13px!important;
  }
}
main .info-bar__item div {
    font-size: 12px;
    font-weight: 500;
    margin-left: 0;
    text-transform: none;
}
main .info-bar__item div .info-bar__title {
    font-family: inherit;
    font-size: 14px;
    font-weight: 600;
    font-style: normal;
    color: #000000;
}
/*Tarja informações alinhamento*/
.info-bar:first-child .info-bar__nav, .info-bar__nav {
    flex-wrap: wrap;
    justify-content: space-around;
}
@media (max-width: 575.98px){
main .info-bar__nav .col-auto {
    padding-left: 0px;
    padding-right: 0px;
  }}
/*cor icones informações*/
.rounded-icon {
    color: #f2a1b2;
    border: 2px solid #f2a1b2;
    margin-right: 6px;
}

/*Tamanho contener informações*/
@media (min-width: 1200px) {
.container, .container-lg, .container-md, .container-sm, .container-xl {
    max-width: 1024px;
  }
}

/*posição botão comprar mobile*/
.product-card--small .product-card__buy {
    position: initial;
    right: 0;
}

/*botão menu mobile todas categorias*/
.menu__link {
    background: #f2a1b2;
    border-radius: 10px;
    font-weight: 500;
    color: #ffffff;
    border-top: 0;
}

/*barra searche desktop*/
main .header__search {
    border-bottom: 0;
    background: 0;
}
.header__search-input {
    background: #a930300a;
}

/*menu desktop*/
div>.header__nav--full {
    padding-top: 20px;
}
div>.header__nav .header__category {
    font-size: 13px;
}
div>.header__nav--full a {
    padding: 0 30px;
    margin-top: 0px;
}
div>.header__nav--full:before {
    margin-top: -8px;
}
.header__container {
    margin-bottom: 0;
}

/*oferta relâmpago*/
.timer__count {
    background: #00000000;
    border-radius: 15px;
    color: #ffffff;
    display: inline-block;
    font-size: var(--h3);
    padding: var(--spacer-1) var(--spacer-2);
    font-weight: 600;
}
.timer__icon {
    display: none;
}
.timer__count {
    margin-top: 10px;
}
.timer__count:before {
    content: "ACABA EM: ";
    font-size: 15px;
    color: #725027;
}

/*principais categorias página principal*/
@media (min-width: 1200px){
.container, .container-lg, .container-md, .container-sm, .container-xl {
    max-width: 1140px;
 }
}

@media (min-width: 992px){
.col-12.col-lg-2.mb-3.mb-lg-0 {
    max-width: 12%!important;
 }
}
.col-12.col-lg-2.mb-3.mb-lg-0 {
    max-width: 25%;
    margin: auto;
    padding-bottom: 20px;
}

.col-12.col-lg-2.mb-3.mb-lg-0:hover {
    opacity: 50%;
}

@media (min-width: 992px)
.mb-lg-0, .my-lg-0 {
    margin: auto;
    padding-bottom: 20px;
}

@media (min-width: 768px){
.page-title.container.mt-4.mb-4.mb-lg-5 {
    padding-top: 50px;
  }}

.page-title.container.mt-4.mb-4.mb-lg-5 {
    text-align: center;
    font-family: sans-serif;
    padding-top: 20px;
}

@media (min-width: 768px){
.page-title__head {
    display: block;
  }}

.page-title.container.mt-4.mb-4.mb-lg-5 {
    text-align: center;
    font-family: sans-serif;
    padding-top: 20px;
    margin-bottom: auto;
    margin-top: 0px;
}

.page-title__short-description.lead {
    font-size: 15px;
    padding-bottom: 10px;
}

/*fonte preço comparação página inicial*/
main .product-card .prices__compare s {
    font-size: 15px;
}
/*espaço preço comparação página inicial*/
main .product-card .prices__compare {
    margin-right: 5px;
}

/*preço parcela página inicial*/
.product-card .prices__installments:before {
    content: "ou";
}

/*cor preço parcela e pix página produto*/
.product__prices .prices__discount span, .product__prices .prices__installments span {
    color: #373435;
}





@media (max-width: 990px){
#ppts .ppts-divider h1.ppts-title {
    font-size: 45px!important;
 }
}
@media (min-width: 992px){
.pt-lg-3, .py-lg-3 {
    padding-top: 1rem!important;
 }
}

#ppts .ppts-divider {
    border-bottom: 5px solid #4698ca;
}

#ppts .container {
    max-width: 100%;
    width: 1360px;
}

#ppts .ppts-imageCrop {
    height: 635px;
    margin-bottom: -6rem;
    margin-left: -10rem;
    margin-top: -7rem;
    overflow: hidden;
    position: relative;
    width: -moz-fit-content;
    width: fit-content;
}

#ppts .ppts-imageCrop img {
    margin: -82px 0 0;
    position: relative;
}

#ppts .ppts-divider h1.ppts-title {
    color: #005aa7!important;
    font-size: 60px;
    font-weight: 900;
    line-height: normal;
    margin: 0;
    text-align: center;
}

@media (max-width: 990px){
#ppts .ppts-divider .ppts-title+p {
    font-size: 24px!important;
 }
}
#ppts .ppts-divider .ppts-title+p {
    color: #646457;
    font-size: 39.06px;
    font-weight: 400;
    text-align: center;
}

.align-items-center {
    align-items: center;
}

.justify-content-center {
    justify-content: center;
}

#ppts .ppts-compare small {
    color: #005aa7;
    display: block;
    font-size: 34px;
    font-weight: 600;
    line-height: 42px;
    text-align: center;
}
@media (max-width: 990px){
#ppts .ppts-compare .detail {
    font-size: 41px!important;
 }
}
#ppts .ppts-compare .detail {
    background: #c20840;
    border-radius: 20px;
    box-shadow: 0 4px 4px rgb(0 0 0 / 25%);
    color: #fff;
    font-size: 61.04px;
    font-weight: 900;
    line-height: normal;
    margin: 1rem auto;
    padding: 0.5rem 1.5rem;
    position: relative;
    width: -moz-fit-content;
    width: fit-content;
}
@media (max-width: 990px){
#ppts .ppts-compare span {
    font-size: 26px!important;
    line-height: normal!important;
 }
}
#ppts .ppts-compare span {
    color: #646457;
    display: block;
    font-size: 36px;
    font-weight: 400;
    line-height: 47px;
    text-align: center;
}

#ppts .ppts-compare .detail img {
    position: absolute;
}

#ppts .ppts-como_participar-bg {
    background: #4698ca;
}

@media (max-width: 990px){
#ppts .ppts-como_participar .ppts-title {
    font-size: 46px!important;
    padding: 3rem 0 5rem!important;
    text-align: center!important;
 }
}
#ppts .ppts-como_participar .ppts-title {
    color: #ffbf3f;
    font-size: 61.04px;
    font-weight: 900;
    line-height: 73px;
    margin: 3rem 0;
}
@media (max-width: 990px){
#ppts .ppts-como_participar ul {
    flex-direction: column!important;
    margin: -6rem 0 0!important;
    padding: 0 2rem!important;
    width: 100%!important;
 }
}
#ppts .ppts-como_participar ul {
    align-items: center;
    display: flex;
    gap: 1rem;
    margin: 3rem 0 1.5rem -10rem;
}

#ppts ul {
    list-style-type: none;
}
@media (max-width: 990px){
#ppts .ppts-como_participar ul li {
    width: 100%!important;
  }
}
#ppts .ppts-como_participar ul li {
    background: #f8f8f8;
    padding: 20px;
    width: 33.33333333%;
}
#ppts .ppts-como_participar ul li strong {
    color: #4698ca;
    display: block;
    font-size: 48.83px;
    font-weight: 900;
    line-height: 59px;
}
#ppts .ppts-como_participar ul li b {
    color: #4698ca;
    font-size: 19px;
    line-height: 23px;
}
#ppts .ppts-como_participar ul li p {
    color: #6a6a57;
    font-size: 14px;
    line-height: 22px;
}
@media (max-width: 990px){
#ppts .ppts-info {
    margin: 2rem auto 0 0!important;
    text-align: center!important;
 }
}
#ppts .ppts-info {
    color: #4698ca;
    font-size: 19.2px;
    font-weight: 900;
    line-height: 23px;
}
#ppts .ppts-info small {
    color: #646457;
    font-size: 15.36px;
    font-weight: 400;
    line-height: 20px;
}
#ppts .ppts-benefits img {
    display: block;
    margin: 0 auto 1rem;
}
#ppts .ppts-benefits strong {
    color: #4698ca;
    display: block;
    font-size: 16px;
    font-weight: 600; 
    line-height: 24px;
    margin: 0 auto;
    max-width: 100%;
    text-align: center;
    width: 220px;
}

@media (min-width: 768px){
.mt-md-5, .my-md-5 {
    margin-top: 0px!important;
 }
}
