css:
/*Tarja Informações*/
.info-bar {
    background: #ffffff;
    color: #000;
    margin-left: auto;
    margin-right: auto;
    max-width: var(--content-max-width);
    width: 100%;
}

/*Tamanho FAQ*/
.pt-1, .py-1 {
    padding-top: 0rem;
    margin-top: 0px;
}

/*remover opções de pagamento página produto*/
.product__payment.card.mb-3 {
display: none
}

/*Esconder botão de cancelamento de pedido*/
.order-info__toggle {
    display: none !important;
}

/*Botão comprar pagina inicial mobile*/
@media screen and (max-width: 480px) {
.products-carousel__item  .product-card__buy.fade {
  opacity: 1 !important;
}}

/*diminuir espaço pagina inicial*/
.mb-5, .my-5 {
    margin-bottom: 2rem;
}

/*remover imagens forma de pagamento*/
.payment__stamp {
    display: none;
}
.payment__back, .payment__intermediator {
    display: none;
}

/*cor botão comprar página produto - final*/
.product__sticky-buy .btn-primary {
background-color: #26ad70;
border-color: #26ad70;
}
.product__sticky-buy .btn-primary:hover{
background-color: #26ad70;
border-color: #26ad70;
}

/*cor botão comprar página produto - inicio*/
.product__buy button {
color: #ffffff;
background-color: #26ad70;
border-color: #26ad70;
width: 100%;
}
.product__buy button:hover {
background: #26ad70;
border-color: #26ad70;
}

/*cor botão comprar página inicial*/
.product-card__buy button {
background-color: #26ad70;
border-color: #26ad70;
color: #fff;
border-radius: 0.1rem;
font-size: 0.8rem;
padding: 0.36rem 0.75rem;
}
.product-card__buy button:hover {
background-color: #26ad70;
border-color: #26ad70;
}

/*cor botão finalizar comprar carrinho*/
.btn-primary {
    background-color: #26ad70;
    border-color: #26ad70;
}

/*tag circulo desconto*/
.product-card__offer-stamp {
    border-radius: 1rem!important;
    color: #fff!important;
    line-height: 2.5!important;
    background-color: #353a42;
}

/*Cor descrição página produto*/
@media (max-width: 990px)
#product-description .lead, h1 {
    font-size: calc(var(--h1)*.6)!important;
}
#product-description .lead {
    color: #f2a1b2;
    margin-bottom: 0;
    font-weight: 700;
}

/*Cor especificação página produto*/
@media (max-width: 990px)
#product-specs .lead, h1 {
    font-size: calc(var(--h1)*.6)!important;
}
#product-specs .lead {
    color: #f2a1b2;
    margin-bottom: 0;
    font-weight: 700;
    margin: 0px 0px 35px 0px;
}

/*Tamanha do preço na página produto*/
.prices--big .prices__value {
    font-size: calc(var(--h1)*.8);
}

/*Tamanha do descrição na página produto*/
.h1, h1 {
    font-size: 2rem;
}
@media (max-width: 990px) {
.h1, h1 {
    font-size: calc(var(--h1)*.6)!important;
 }
}

/*Negrito titulo informações pagina inicial*/
.info-bar__title {
    font-weight: 600;
}

/*Cor search*/
.header__search {
    border-bottom: 1px solid #000000;
}
/*Cor cabeçalho desktop*/
.header {
    background: #ffffff;
    border-bottom: 0px solid #ffffff;
}

/*Cor menu desktop*/
.header__category:hover {
    background: #ffffff;
    text-decoration: none;
    color: #f2a1c3;
}

/*espaço menu desktop*/
.header__nav--full a {
    padding: 10px 30px 0px 30px;
}

/*fonte menu desktop*/
.header__nav a {
    font-weight: normal;
}

/*tamanho topbar cabeçalho*/
.top-bar__countdown {
    padding-bottom: 4px;
    padding-top: 4px;
    font-size: 16px;
}
@media only screen and (max-width: 900px){
.top-bar__countdown {
    padding-bottom: var(--spacer-2)!important;
    padding-top: var(--spacer-2)!important;
    font-size: var(--font-size-sm)!important;
    padding: var(--spacer-1) var(--spacer-2)!important;
  }
}

/*cor frete carrinho*/
.list-group-item.active {
    background-color: #f2a1b2;
    border-color: #f2a1b2;
    color: #000;
}

.joiasmilan-loja-joiasmilan-3-x-specLine:nth-child(odd) {
    background: rgba(222,163,156,.3);
}

/*especificação página produto*/
.joiasmilan-loja-joiasmilan-3-x-specLine {
    display: flex;
    align-items: flex-start;
    justify-content: space-between;
    padding: 1% 5%;
    border-radius: 99px;
    margin: 0px 500px 0px 0px;
}

@media screen and (max-width: 900px) {
.joiasmilan-loja-joiasmilan-3-x-specLine {
    padding: 3% 5%!important;
    margin: 0px 0px 0px 0px!important;
  }
}

/*cor disponibilidade estoque*/
.product__short-stock {
    background: none;
    font-weight: normal;
}

/*selos rodapé*/
@media (max-width: 990px) {
.app__stamps>.stamps {
    justify-content: center;
 }
}

.stamps {
    display: flex;
}

.stamps {
    flex-wrap: wrap;
}
@media (max-width: 990px) {
.stamps>li {
    flex: 0 0 calc(50% - 10px);
    min-width: auto;
    width: auto;
 }
}  
.stamps>li {
    align-items: center;
    background: #fff0;
    border-radius: 3px;
    justify-content: space-between;
    margin: 0 10px 10px 0!important;
    padding: 10px;
    text-align: center;
}
@media (min-width: 990px) {
.app__stamps>.stamps {
    justify-content: center;
 }
}
@media (min-width: 990px) {
.app__footer .stamps>li {
    flex: 0 0 auto;
 }
}

/*cor icones informações*/
.rounded-icon {
    border: 1px solid #f8f9f900;
    color: #f2a1b2;
}

/*preço página inicial*/
.prices__value {
    line-height: 1.5;
}
.prices, .prices__installments {
    font-weight: 500;
}
