options.footer:
@media screen and (max-width: 480px)
.products-carousel .product-card__buy .btn-primary {
    font-size: .7rem;
}

.btn:not(:disabled):not(.disabled) {
    cursor: pointer;
}
@media (max-width: 990px)
.product-card .product-card__buy>* {
    justify-content: space-between;
    width: 100%!important;
}
.product-card__buy .btn-primary {
    line-height: 36px;
    min-width: unset;
    padding: 0;
    width: auto;
}
@media (max-width: 990px)
.product-card__buy .btn-primary {
    font-size: 10px;
    height: auto;
    line-height: normal;
    min-width: unset;
    padding: 8px 18px;
    width: auto;
}
.product-card__buy .btn-primary {
    width: -webkit-fit-content;
    width: -moz-fit-content;
    width: fit-content;
}
.product-card__buy .btn-primary {
    background: var(--apx_laranja2);
    border: none;
    border-radius: 47px;
    color: #fff;
    font-family: Averta;
    font-size: 14px;
    font-weight: 700;
    height: auto;
    letter-spacing: .15em;
    line-height: 47px;
    min-width: 180px;
    padding: 0 25px;
    text-transform: uppercase;
    transition: opacity .2s;
}
